  --Check-Background-To: hsl(280, 87%, 65%);
  --white: hsl(0, 0, 100%);
  --white-logo: hsl(0, 0, 100%);
}

// font variables
@import url("https://fonts.googleapis.com/css2?family=Rubik:wght@400;500;700&display=swap");
$font-primary: "Rubik", sans-serif;
$fontsize-textinput: 18px;

// transition
$primary-transition: all 1s ease-in-out;

// breakpoint used in media queries
$breakpoints: (
  "tablet": 43em,
  "laptop": 62em,
  "desktop": 82em,
