  }

  &__header-wrapper {
    padding-top: 2.6rem;
    animation: backInUp;
    animation-duration: 2s;
  }

  // positioned relative because contain todo__nav
  &__items-wrapper {
    @include v.flex-container();
    margin-bottom: 2rem;
    position: relative;
  }

  &__logo {
    font-size: 1.7rem;
    letter-spacing: 0.75rem;
    font-weight: 500;
    color: #fff;
  }

  &__theme-switch {
    width: 1.6rem;
    height: 1.6rem;
    background: url("../assets/images/icons8-settings.svg") center / contain
      no-repeat;
    z-index: 10;
    &:focus {
      animation: reverseRotation 1s ease-in-out 1;
    }

    &--active {
      @extend .todo__theme-switch;
      &:focus {
        animation: rotation 1s ease-in-out 1;
      }
    }
  }

  &__form {
    display: inline-block;
    margin-bottom: 2.45rem;
    border-radius: 1rem;
    border: none;
    width: 100%;
    position: relative;
  }

  &__input-wrapper {
    display: grid;
    grid: 1fr / 17% auto;
    align-items: center;
    justify-items: center;
    background-color: var(--list-bg);
    overflow: hidden;
    border-radius: 0.5rem;
  }

  &__circle {
    position: relative;

    &-fill {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 1.2rem;
      height: 1.2rem;
      border-radius: 1rem;
      border: 1px solid var(--text-gray);

      &--active {
        @extend .todo__circle-fill;
        &:hover {
          border: 1px solid var(--Bright-Blue);
        }
      }

      &--disabled {
        @extend .todo__circle-fill;
      }
    }
  }

  &__input {
    width: 100%;
    padding: 1.2rem 0;
    border: none;
    background-color: var(--list-bg);
    font-size: 0.9rem;
    color: var(--text-color);

    /*|| making the styles for autofill consistant with the design*/
    &:-webkit-autofill,
    -webkit-autofill:focus,
    -webkit-autofill:hover {
      -webkit-text-fill-color: var(--text-color);
      -webkit-box-shadow: 0 0 0px 1000px var(--list-bg) inset;
      transition: background-color 5000s ease-in-out 0s;
    }
  }

  /*|| overlay */

  .todo__form-animation {
    width: 100%;
    height: 100%;
    background-color: var(--list-bg);
    position: absolute;
    top: 0;
    z-index: -1;

    &--active {
      @extend .todo__form-animation;
      transform: translateY(100px);
      transition: all 500ms linear;
      z-index: 1;
    }
  }

  /*------------------------------*\
    #todo__nav
  \*------------------------------*/
  &__nav {
    position: absolute;
    width: 0;
    height: 0;
    max-height: 2.5rem;
    top: 50%;
    right: 0;
    transform: translateY(-50%);
    @include v.flex-container();
    border-radius: 10rem 0 0 10rem;
    visibility: hidden;
    transform-origin: right right;
    transition: v.$primary-transition;

    &--active {
      @extend .todo__nav;
      visibility: visible;
      width: 38%;
      height: 100%;
      transform: translateY(-50%);
      border-radius: 0.5rem;
    }

    &-item {
      flex: 1;
      height: 100%;
    }

    &-icon {
      width: 100%;
      height: 100%;
      @include v.flex-container(
        $al-con: center,
        $js-con: center,
        $flex-d: column
      );

      &:hover {
        transition: all 1s ease;
        transform: scale(1.2) translateY(-20%);
      }

      &--sun {
        background: url("../assets/images/icon-sun.svg") no-repeat center;
      }

      &--moon {
        background: url("../assets/images/icon-moon.svg") no-repeat center;
      }

      &--custom {
        background: url("../assets/images/icon-adjustment.png") center / contain
          no-repeat;
      }
    }
  }

  /*------------------------------*\
    #todo__modal 
  \*------------------------------*/
  &__modal {
    transition: v.$primary-transition;
    position: absolute;
    left: 50%;
    top: 150%;
    transform: translate(-50%, -50%);

    @include v.respond-to("laptop") {
      top: 100%;
    }
  }

  /*------------------------------*\
    #todo__list
  \*------------------------------*/

  &__main {
    position: absolute;
    width: 100%;
    top: 85.5%;
    animation: backInLeft;
    animation-duration: 1s;
  }

  &__list {
    border-radius: 0.4rem;
    background: var(--list-bg);
    color: var(--text-color);
  }

  &__footer-heading {
    text-align: center;
    font-size: 1rem;
    color: var(--text-completed-primary);
    margin-top: 2.5rem;
    font-weight: 300;
  }

  &__item {
    border-bottom: 1px solid var(--text-completed-primary);
    &-wrapper {
      display: grid;
      grid: 1fr / 17% auto 16%;
      align-items: center;
      padding: 1rem 0;
      min-height: 3.25rem;
    }

    &-circle {
      justify-self: center;

      &--active {
        @extend .todo__item-circle;

        /*|| adding the check icon*/
        .todo__item-circle-fill {
          background: url("../assets/images/icon-check.svg") no-repeat center,
            linear-gradient(
              112deg,
              var(--Check-Background-From),
              var(--Check-Background-To)
            );
        }
      }
    }

    &-text {
      text-align: left;
      font-size: 0.9rem;
      flex: 3;

      &:hover {
        & + .todo__item-btn {
          display: block;
        }
      }

      &--complete {
        text-decoration: line-through;
        color: var(--text-completed-primary);
        text-align: left;
      }
    }

    &-btn {
      display: none;

      &:hover {
        display: block;
      }
    }
  }

  /*------------------------------*\
    #todo__features
  \*------------------------------*/
  &__features {
    background-color: var(--main-bg);
    color: var(--text-completed-primary);
    &-header {
      @include v.flex-container();
      background-color: var(--list-bg);
      padding: 1.1rem;
      border-radius: 0 0 0.4rem 0.4rem;
    }

    &-heading {
      font-size: 0.7rem;
      font-weight: 500;
      word-spacing: 0.2rem;
    }

    &-btn {
      background-color: inherit;
      color: var(--text-completed-primary);
      font-size: 0.75rem;
      font-weight: 700;

      &--hide {
        display: none;
      }

      &--m {
        font-size: 0.9rem;
      }

      &--active {
        color: var(--Bright-Blue);
      }
    }

    &-main {
      transition: all 300ms cubic-bezier(0.4, 0, 1, 1);
      border-radius: 0.4rem;

      position: relative;
      top: 50%;
      left: 50%;
      transform: translate(-50%, 35%);
      background-color: var(--list-bg);
      padding: 1rem;

      &-wrapper {
        @include v.flex-container();
        max-width: 10rem;
        margin: 0 auto;
      }
    }
  }

  /*------------------------------*\
    #tablet styles
  \*------------------------------*/
  @include v.respond-to("tablet") {
    &__header {
      background: var(--header-bg-image-desktop) top center / cover no-repeat;
    }

    &__theme-switch {
      width: 2rem;
      height: 2rem;
    }
    &__input-wrapper {
      grid: 1fr/13% auto;
    }

    &__form {
      margin-bottom: 4.5rem;
    }

    &__circle {
      &-fill {
        width: 1.6rem;
        height: 1.6rem;
      }
    }

    &__nav {
      &--active {
        width: 32%;
      }
    }

    &__main {
      top: 82%;
    }

    &__footer-heading {
      margin-top: 1rem;
    }

    &__item-wrapper {
      min-height: 3.9rem;
      grid: 1fr/13% auto 10%;
    }

    &__item-text {
      font-size: 1rem;
    }

    &__item-btn {
      height: 1.2rem;
    }

    &__features {
      position: relative;

      &-header {
        margin-bottom: 0;
        padding: 1.6rem 1.6rem 1rem 1.6rem;
      }

      &-main {
        padding: 0;
        padding-top: 0.5rem;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        width: 54%;
      }
    }
  }

  /*------------------------------*\
    #laptop styles
  \*------------------------------*/
  @include v.respond-to("laptop") {
    &__header-wrapper {
      padding-top: 4.3rem;
    }

    &__items-wrapper {
      margin-bottom: 2.5rem;
    }

    &__logo {
      font-size: 2.5rem;
      letter-spacing: 1.1rem;
    }

    &__input {
      padding: 1.5rem 0;
      font-size: 1.125rem;
    }
  }
}

