/*------------------------------*\
    #dark-theme
\*------------------------------*/

.dark-theme {
  --main-bg: hsl(235, 21%, 11%);
  --list-bg: hsl(235, 24%, 19%);
  --text-color: hsl(234, 39%, 85%);
  --text-hover: hsl(236, 33%, 92%);
  --text-gray: hsl(234, 11%, 52%);
  --text-completed-primary: hsl(233, 14%, 35%);
  --text-completed-seconday: hsl(237, 14%, 26%);
  --header-bg-image-mobile: url("../assets/images/bg-mobile-dark.jpg");
  --header-bg-image-desktop: url("../assets/images/bg-desktop-dark.jpg");
  --theme-icon: url("../assets/images/icon-sun.svg");
  --white: hsl(0, 0, 100%);
}

